import { useSelector } from "react-redux";
import { Navigate, Outlet } from "react-router-dom";
import Navbar from "../components/Navbar";
import { useDispatch } from "react-redux";
import { authActions,logout } from "../store/authSlice";
import { useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import api from "../service";

function ProtectedPages(){

    const dispatch = useDispatch();
    const logout_id = useSelector(state=>state.auth.user.id);
    useEffect(() => {
      const authenticateUser = async () => {
        try {
          console.log("protected route render...");
            axios.defaults.withCredentials = true;
            const response = await api.get("/authenticate", {
              withCredentials: true,
            });
            console.log(response);
            console.log("setting login state again");
            dispatch(authActions.login({
              id:response.data.id,
              email:response.data.email,
          }));

        } catch (error) {
          toast.error("Session expired please login again");
          dispatch(logout(logout_id));
        }
      };

      authenticateUser();
    }, [dispatch]);

    console.log("checking state");
    const isAuthenticated = useSelector((state)=>state.auth.isAuthenticated);

return(
    <>
        <Navbar />

        {isAuthenticated ? <Outlet />:<Navigate to="/login" />}
    </>

);
}

export default ProtectedPages;
