import React, { useState } from "react";
import { Box, Button, Typography, MobileStepper, Paper, Container } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";

const DashboardPage = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);

  const handleMenuClick = () => {
    navigate("/menu");
  };

  const products = [
    {
      label: "Chocolate Cake",
      description:
        "A rich, moist chocolate cake layered with creamy chocolate frosting, perfect for celebrations or indulgent treats.",
      imgPath: "/images/chocolate_cake.jpeg",
    },
    {
      label: "Blueberry Muffins",
      description:
        "Freshly baked muffins bursting with juicy blueberries, offering a delightful balance of sweetness and tartness.",
      imgPath: "/images/blueberry_muffins.jpeg",
    },
    {
      label: " Handmade Breads",
      description:
        "Soft, golden-brown loaves baked to perfection, bringing the comforting taste of freshly made bread to your table.",
      imgPath: "/images/handmade_breads.jpeg",
    },
  ];

  const handleNext = () => {
    setActiveStep((prevActiveStep) =>
      prevActiveStep === products.length - 1 ? 0 : prevActiveStep + 1
    );
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) =>
      prevActiveStep === 0 ? products.length - 1 : prevActiveStep - 1
    );
  };

  return (
    <Box>
      {/* Full-screen image */}
      <Box
        sx={{
          backgroundImage: "url('/images/bakery_home.webp')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "100vh",
        }}
      />

      {/* Today's Menu Section */}
      <Box
        sx={{
          background: 'linear-gradient(135deg, #f6d365 0%, #fda085 100%)',
          py: 4,
          textAlign: 'center',
          boxShadow: 3,
        }}
      >
        <Container>
          <Typography 
            variant="h4" 
            component="h2" 
            gutterBottom
            sx={{
              color: 'white',
              fontWeight: 'bold',
              textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
            }}
          >
            Today's Menu
          </Typography>
          <Button
            onClick={handleMenuClick}
            variant="contained"
            sx={{
              background: 'white',
              color: '#f6a261',
              fontWeight: 'bold',
              px: 4,
              py: 2,
              borderRadius: '30px',
              boxShadow: 3,
              '&:hover': {
                background: '#fff3e0',
                boxShadow: 4,
              },
            }}
          >
            Explore Full Menu
          </Button>
        </Container>
      </Box>

      {/* Best Products Section */}
      <Box 
        sx={{
          background: 'linear-gradient(135deg, #f5f7fa 0%, #e6e9f0 100%)',
          py: 6,
        }}
      >
        <Container>
          <Typography 
            variant="h4" 
            component="h2" 
            align="center" 
            gutterBottom
            sx={{
              mb: 4,
              color: '#333',
              fontWeight: 'bold',
            }}
          >
            Our Best Sellers
          </Typography>

          <Box sx={{ maxWidth: 800, flexGrow: 1, margin: "0 auto" }}>
            <Paper
              square
              elevation={0}
              sx={{
                display: "flex",
                alignItems: "center",
                height: 50,
                pl: 2,
                bgcolor: "background.default",
              }}
            >
              <Typography variant="h6" color="primary">
                {products[activeStep].label}
              </Typography>
            </Paper>

            <Box
              sx={{
                height: 400,
                maxWidth: 800,
                width: "100%",
                position: "relative",
                overflow: "hidden",
                borderRadius: 2,
                boxShadow: 3,
              }}
            >
              <Box
                component="img"
                sx={{
                  display: "block",
                  maxWidth: 800,
                  overflow: "hidden",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                }}
                src={products[activeStep].imgPath}
                alt={products[activeStep].label}
              />
              <Box
                sx={{
                  position: "absolute",
                  bottom: 0,
                  left: 0,
                  right: 0,
                  bgcolor: "rgba(0,0,0,0.6)",
                  color: "white",
                  p: 2,
                }}
              >
                <Typography variant="h6">{products[activeStep].label}</Typography>
                <Typography>{products[activeStep].description}</Typography>
              </Box>
            </Box>

            <MobileStepper
              variant="text"
              steps={products.length}
              position="static"
              activeStep={activeStep}
              nextButton={
                <Button size="small" onClick={handleNext}>
                  Next
                  <KeyboardArrowRight />
                </Button>
              }
              backButton={
                <Button size="small" onClick={handleBack}>
                  <KeyboardArrowLeft />
                  Back
                </Button>
              }
            />
          </Box>
        </Container>
      </Box>
    </Box>
  );
};

export default DashboardPage;
