import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import Cookies from "js-cookie";
import api from "../service";

export const logoutUser = createAsyncThunk(
    'logoutUser',
    async (_, { getState, rejectWithValue }) => {  // Using getState instead of parameter
      try {
        const state = getState();
        const email = state.auth.user.id;  // Get email from Redux state
        const response = await api.post("/logout", { id });
        return response.data;
      } catch (error) {
        return rejectWithValue(error.response.data);
      }
    }
  );

const initialState = {
    user: {
        id:"",
        email:""
    },
    isAuthenticated:false
}

const authSlice = createSlice({
    name:"auth",
    initialState:initialState,
    reducers:{
        login(state,actions){
            console.log("inside login...");
            state.isAuthenticated = true;
            state.user = {
                id: actions.payload.id,
                email: actions.payload.email
            }
        },
        logout(state){
            // try{
                // const response = await api.post("/logout",{email});
                // console.log(response.data.message);
                Cookies.remove("accessToken",{path:"/"});
                Cookies.remove("refreshToken",{path:"/"});
                state.isAuthenticated = false;
                state.user = {
                    id:"",
                    email:""
                };
            // }
            // catch(e){
            //     Cookies.remove("accessToken",{path:"/"});
            //     Cookies.remove("refreshToken",{path:"/"});
            //     state.isAuthenticated = false;
            //     state.user = {
            //         id:"",
            //         email:""
            //     };
            // }
        }
    }
})

export const authActions = authSlice.actions;
export default authSlice;
