import { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../service/authService";
import { useDispatch } from "react-redux";
import { authActions } from "../store/authSlice";
import { login } from "../service/authService";
import { useMutation } from "@tanstack/react-query";

function LoginPage(){

    const [formData, setFormData] = useState({
        email:"",
        password:""
    })

    const [errors, setErrors] = useState({
        email:"",
        password:"",
        general:""
    })

    const {mutate} = useMutation({
      mutationFn:login,
      onError:(error)=>{
        const errorMessage = error.response?.data?.message || 'Registration failed. Please try again.';
        toast.error(errorMessage);
      },
      onSuccess:(data)=>{

      }
    })

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleChange = (e) =>{
        const {name,value} = e.target;
        setFormData(prev => ({
            ...prev,
            [name]:value
        }))

        if(errors[name]){
            setErrors(prev => ({
                ...prev,
                [name]:""
            }))
        }
    };

    const validateForm = () =>{
        let tempError = {};
        let isValid = true;

        if(!formData.email){
            tempError.email = "Email is required";
            isValid = false;
        } else if(!/\S+@\S+\.\S+/.test(formData.email)) {
            tempError.email = "Email is invalid";
            isValid = false;
        }

        if(!formData.password){
            tempError.password = "Password is required";
            isValid = false;
        } else if(formData.password.length < 8 ) {
            tempError.password = "Password should be minimun 8 characters long";
            isValid = false;
        }

        setErrors(tempError);
    return isValid;
    }

    const handleSubmit = async(e) => {
        e.preventDefault();

        if(!validateForm()){
            return;
        }

        const credentials = {
          ...formData
        };

        try{
          const response = await authService.login(credentials);
          dispatch(authActions.login(response.email));
          navigate("/dashboard");
        }
        catch(error){
          let tempError = {};
          if (error.response) {
            console.error('Error response:', error.response.data);
            const { status, message, timestamp } = error.response.data;

            if (status === 403) {
              tempError.general = `${message}`;
            } else {
              tempError.general = `Unexpected error: ${message}`;
            }
        } else if (error.request) {
            tempError.general = 'No response from the server. Please try again later.';
        } else {
          tempError.general = `An error occurred: ${error.message}`;
        }

        setErrors(tempError);
        }
    }

return(
    <div className="container">
    <div className="row justify-content-center align-items-center min-vh-100">
      <div className="col-12 col-md-6 col-lg-4">
        <div className="card">
          <div className="card-header text-center">
            <h3 className="mb-0">Login</h3>
          </div>
          <div className="card-body">
            {errors.general && (
              <div className="alert alert-danger" role="alert">
                {errors.general}
              </div>
            )}

            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  disabled={isLoading}
                />
                {errors.email && (
                  <div className="invalid-feedback">{errors.email}</div>
                )}
              </div>

              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input
                  type="password"
                  className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  disabled={isLoading}
                />
                {errors.password && (
                  <div className="invalid-feedback">{errors.password}</div>
                )}
              </div>

              <button 
                type="submit" 
                className="btn btn-primary w-100"
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Loading...
                  </>
                ) : 'Login'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
)
}
export default LoginPage;
