// src/services/api.js
import axios from 'axios';
import Cookies from 'js-cookie';
import { Navigate } from 'react-router-dom';

const api = axios.create({
  baseURL: 'http://localhost:8080/',
  headers: {
          'Content-Type': 'application/json'
            }
 // Replace with your API base URL
});

// Add a request interceptor
api.interceptors.request.use(
  (config) => {
    config.withCredentials = true;
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;

    // Check for your specific error code/status
    // const isTokenExpired = 
    //   error.response?.data?.code === 'TOKEN_EXPIRED' || // If sending custom error code
    //   error.response?.status === 777;                   // If sending custom status code

      const isTokenExpired = error.response?.data?.status === 777;// If sending custom status code
      console.log("error... in interceptor");
      console.log(error.response.status);
      console.log(isTokenExpired);
    // Only attempt refresh for token expiration
    if (isTokenExpired && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        const response = await axios.post('/refresh', {}, {
          withCredentials: true
        });
        console.log("/refresh has been hit...")
        const { token } = response.data;
        // localStorage.setItem('token', token);
        // api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        // Retry original request
        // originalRequest.headers['Authorization'] = `Bearer ${token}`;
        return api(originalRequest);

      } catch (refreshError) {
        // localStorage.removeItem('token');
        Cookies.remove("accessToken");
        Cookies.remove("refreshToken");
        const navigate = Navigate();
        navigate("/login");
        // window.location.href = '/login';
        return Promise.reject(refreshError);
      }
    }

    // For all other errors, reject normally
    return Promise.reject(error);
  }
);

// api.interceptors.response.use(
//     (response) => response,
//     (error) => {
//       // Handle errors globally\\\\\\\\\\\\\
//       console.error('API Error:', error);
//       return Promise.reject(error);
//     }
//   );

export default api;
