import "./App.css";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import HomePage from "./pages/Home";
import LoginPage from "./pages/Login";
import SignupPage from "./pages/Signup";
import ErrorPage from "./pages/Error";
import DashboardPage from "./pages/Dashboard";
import { useDispatch } from "react-redux";
import { authActions } from "./store/authSlice";
import { useEffect } from "react";
import axios from "axios";
import ProtectedPages from "./layout/Protected";
import UnprotectedPages from "./layout/Unprotected";
import { QueryClient } from "@tanstack/react-query";
import { QueryClientProvider } from "@tanstack/react-query";
import { toast } from "react-toastify";
const router = createBrowserRouter([
  {
    path: "/",
    element: <ProtectedPages />,
    errorElement: <ErrorPage />,
    children: [
      {
        index: true,
        element: <DashboardPage />,
      },
    ],
  },
  {
    path: "/",
    element: <UnprotectedPages />,
    errorElement: <ErrorPage />,
    children: [
      {
        index: true,
        element: <HomePage />,
      },
      {
        path: "/login",
        element: <LoginPage />,
      },
      {
        path: "/signup",
        element: <SignupPage />,
      },
    ],
  },
]);

const queryClient = new QueryClient();

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    const authenticateUser = async () => {
      try {
        console.log("app render");
        const currentPath = window.location.pathname;
        if (!currentPath.includes('/login') && !currentPath.includes('/signup')) {
          axios.defaults.withCredentials = true;
          const response = await axios.get("http://localhost:8080/authenticate", {
            withCredentials: true,
          });
          console.log(response);
          dispatch(authActions.login(response.data.email));
        }
      } catch (error) {
        toast.error("Session expired please login again");
        dispatch(authActions.logout());
      }
    };

    authenticateUser();
  }, [dispatch]);

  return (
    <QueryClientProvider client={queryClient}>
      <RouterProvider router={router} />
    </QueryClientProvider>
  );
}

export default App;
