import React, { useState } from 'react';
import authService, { register } from '../service/authService';
import { useNavigate } from 'react-router-dom';
import { useMutation } from '@tanstack/react-query';

const Signup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ""
  });

  const [errors, setErrors] = useState({
    email:"",
    password:"",
    general:"",
    confirmPassword: ""
  })

  const navigate = useNavigate();

  const {mutate,isLoading,isError,error} = useMutation({
    mutationFn: register
  });
  const handleChange = (e)=>{

    const {name,value} = e.target;
    setFormData(prev => ({
        ...prev,
        [name]:value,
    }))

    if(errors[name]){
        setErrors(prev => ({
            ...prev,
            [name]:""
        }))
    }
  }

  const validateForm = () => {
    let tempError = {};
    let isValid = true;

    if (!formData.username) {
      tempError.username = "Username is required";
      isValid = false;
    } else if (formData.username.length < 3) {
      tempError.username = "Username should be at least 3 characters long";
      isValid = false;
    }

    if (!formData.email) {
      tempError.email = "Email is required";
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      tempError.email = "Email is invalid";
      isValid = false;
    }

    if (!formData.password) {
      tempError.password = "Password is required";
      isValid = false;
    } else if (formData.password.length < 8) {
      tempError.password = "Password should be minimum 8 characters long";
      isValid = false;
    }

    if (!formData.confirmPassword) {
      tempError.confirmPassword = "Please confirm your password";
      isValid = false;
    } else if (formData.password !== formData.confirmPassword) {
      tempError.confirmPassword = "Passwords do not match";
      isValid = false;
    }

    setErrors(tempError);
    return isValid;
  };

  const handleSubmit = async (e) =>{
    e.preventDefault();

    if(!validateForm()){
        return;
    }

    const data = {
        ...formData,
        roleName: "ROLE_USER"
    }
    delete data.confirmPassword;
    console.log(data);

    try{
      // const response = await authService.register(data);

      mutate({event:data});


    setFormData({
      username: '',
      email: '',
      password: '',
      confirmPassword: ""
    });
    setErrors({
      email:"",
      password:"",
      general:"",
      confirmPassword: ""
    })

    navigate("/login");

    }
    catch(error){
      let tempError = {};
      if (error.response) {
        console.error('Error response:', error.response.data);
        const { status, message, timestamp } = error.response.data;

        if (status === 403) {
          tempError.general = `${message}`;
        } else {
          tempError.general = `Unexpected error: ${message}`;
        }
    } else if (error.request) {
        tempError.general = 'No response from the server. Please try again later.';
    } else {
      tempError.general = `An error occurred: ${error.message}`;
    }

    setErrors(tempError);
    }

  }

  return (
    <div className="container">
      <div className="row justify-content-center align-items-center min-vh-100">
        <div className="col-12 col-md-6 col-lg-4">
          <div className="card">
            <div className="card-header text-center">
              <h3 className="mb-0">Sign Up</h3>
            </div>
            <div className="card-body">
              {errors.general && (
                <div className="alert alert-danger" role="alert">
                  {errors.general}
                </div>
              )}
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="username" className="form-label">Username</label>
                  <input
                    type="text"
                    className={`form-control ${errors.username ? 'is-invalid' : ''}`}
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    disabled={isLoading}
                  />
                  {errors.username && (
                    <div className="invalid-feedback">{errors.username}</div>
                  )}
                </div>

                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input
                    type="email"
                    className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    disabled={isLoading}
                  />
                  {errors.email && (
                    <div className="invalid-feedback">{errors.email}</div>
                  )}
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Password</label>
                  <input
                    type="password"
                    className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    disabled={isLoading}
                  />
                  {errors.password && (
                    <div className="invalid-feedback">{errors.password}</div>
                  )}
                </div>

                <div className="mb-3">
                  <label htmlFor="confirmPassword" className="form-label">Confirm Password</label>
                  <input
                    type="password"
                    className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                    id="confirmPassword"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    disabled={isLoading}
                  />
                  {errors.confirmPassword && (
                    <div className="invalid-feedback">{errors.confirmPassword}</div>
                  )}
                </div>

                <button
                  type="submit"
                  className="btn btn-primary w-100"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Loading...
                    </>
                  ) : 'Sign Up'}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
